//This page may refer to Reader code (mainly taken from Reader code)
class LoadContent extends StatefulWidget {
  final String storyID,
      storyLanguage,
      storyTitle,
      storybookDesc,
      storybookGenre,
      storybookCover;
  final String contributorID;
  LoadContent(
      {Key key,
      this.storyID,
      this.contributorID,
      this.storyTitle,
      this.storyLanguage,
      this.storybookDesc,
      this.storybookGenre,
      this.storybookCover})
      : super(key: key);
  @override
  _LoadContentState createState() => new _LoadContentState();
}

class _LoadContentState extends State<LoadContent> {
  String url = 'http://i2hub.tarc.edu.my:8887/mmsr/';
  var db = DBHelper();
  List pageText, languageData, pageLanguage, onGoing;

  List<String> speechID = List<String>();

  @override
  void initState() {
    super.initState();
  }

  Future<List> _getPage() async {
    final r = await http.post(url + "getPage.php", body: { //get page data
      "storybookID": widget.storyID,
      "languageCode": widget.storyLanguage,
    });
    var textData = json.decode(r.body);

    final speechResponse = await http.post(url + "getSpeech.php"); //get speech language
    var speechData = json.decode(speechResponse.body);
    for (int i = 0; i < speechData.length; i++) {
      for (int j = 0; j < textData.length; j++) {
        if (speechData[i]['languageCode'] == textData[j]['languageCode']) {
          speechID.add(speechData[i]['speech_id'].toString());
        }
      }
    }
    return textData;
  }

  @override
  Widget build(BuildContext context) {
    return new Scaffold(
      body: new FutureBuilder<List>(
        future: _getPage(),
        builder: (context, snapshot) {
          if (snapshot.hasData) {
            pageText = snapshot.data;
            return new PageContent(
              pageText: pageText,
              contributorID: widget.contributorID,
              languageData: languageData,
              pageLanguage: pageLanguage,
              storyID: widget.storyID,
              storyLanguage: widget.storyLanguage,
              storyTitle: widget.storyTitle,
              onGoing: onGoing,
              speechID: speechID,
              storybookDesc: widget.storybookDesc,
              storybookGenre: widget.storybookGenre,
              storybookCover: widget.storybookCover,
            );
          } else {
            return new Center(child: SpinKitThreeBounce(color: Colors.blue));
          }
        },
      ),
    );
  }
}

