    // var history = History(widget.childrenID,widget.storyTitle,difference,dateFormat.format(DateTime.now()));
    // db.saveHistory(history);
    // var onGoing = OnGoing(widget.childrenID,widget.storyID,0,0);
    // db.updateOngoing(onGoing);
  }

